settings.custom_liquid:
{% assign vendorImage = "" %}

{% if product.vendor == "Abercrombie & Fitch" %}
  {% assign vendorImage = "Abercrombie-Fitch.svg" %}
{% elsif product.vendor == "INITIO Parfums Privés" %}
  {% assign vendorImage = "INITIO-Parfums-Prives.svg" %}
{% elsif product.vendor == "Bond No. 9" %}
  {% assign vendorImage = "Bond-N-9.svg" %}
{% elsif product.vendor == "Dolce & Gabanna" %}
  {% assign vendorImage = "Dolce-Gabbana.svg" %}
{% elsif product.vendor == "Estée Lauder" %}
  {% assign vendorImage = "Estee-Lauder.svg" %}
{% elsif product.vendor == "Lancôme" %}
  {% assign vendorImage = "Lancome.svg" %}
{% elsif product.vendor == "Edén" %}
  {% assign vendorImage = "Eden.svg" %}
{% elsif product.vendor == "Roger & Gallet" %}
  {% assign vendorImage = "Roger-Gallet.svg" %}
{% else %}
  {% assign vendorImage = product.vendor | append: '.svg' %}
{% endif %}

<div class="vendor-image" style="max-width: 200px;">
  <a href="{{ shop.url }}/collections/{{ product.vendor | handle }}">
    <img src="{{ vendorImage | asset_url }}" alt="{{ product.vendor }}">
  </a>
</div>

{% if product.tags contains 'Preventa' %}
  <div class="green">
   <h3>!Nuevo lanzamiento en preventa!</h3>
<p>Fecha estimada de llegada: <strong>Jueves 12 de diciembre de 2024.</strong></p>
  </div>
{% endif %}

{%- if product.metafields.custom.pa_s -%}
{%- assign designer_handle = product.metafields.custom.pa_s | replace:" ", "-" -%}
{{ url[pa_s_handle].content }}
{%- else -%}
{%- endif -%}




<div class="container" id="especificaciones">
<div class="row">
<div class="col-md-12">
<h2>Descripción<br>
olfativa</h2>
</div>
</div>
<div class="row">
<div class="col-md-6 col-12" style="padding-right: 7%;">
{% if product.description  != blank -%}
<h3>Descripción</h3>
<p>{{ product.description }}
{%- else -%}
{%- endif %}
</p>
{% if product.metafields.custom.notas_y_ocasi_n != blank -%}
<h4>Notas y ocasión</h4>
<p>{{ product.metafields.custom.notas_y_ocasi_n | metafield_text }}
{%- else -%}
{%- endif %}
</p>
</div>

<div class="col-md-6 col-12 atributos">
  <ul>
<h3 style="padding-top: 10px;">Acerca del perfume</h3>
{% if product.metafields.custom.genero != blank -%}
<li><h4><span class="generoicon"></span>Genero</h4>
<p><a href="/collections/{{ product.metafields.custom.genero | handle }}">{{ product.metafields.custom.genero | metafield_text }}</a>
{%- else -%}
{%- endif %}
</p>
</li>

{% if product.metafields.custom.categoria_de_la_marca != blank -%}
<li><h4><span class="marcaicon"></span>Categoria de la marca</h4>
<p><a href="/collections/{{ product.metafields.custom.categoria_de_la_marca | handle }}">{{ product.metafields.custom.categoria_de_la_marca | metafield_text }}</a>
{%- else -%}
{%- endif %}
</p>
</li>

{% if product.metafields.custom.clima != blank -%}
<li><h4><span class="climaicon"></span>Clima</h4>
{% for clima in product.metafields.custom.clima.value %}
<p><span><a href="/collections/{{ clima | handle }}">{{ clima }},</a></span>
{% endfor %}
{%- else -%}
{%- endif %}
</p>
</li>

{% if product.metafields.custom.acordes_principales != blank -%}
<li><h4><span class="climaicon"></span>Acordes principales</h4>
{% for acordes_principales in product.metafields.custom.acordes_principales.value %}
<p><span><a href="/collections/{{ acordes_principales| handle }}">{{ acordes_principales }},</a></span>
{% endfor %}
{%- else -%}
{%- endif %}
</p>
</li>

{% if product.product.metafields.custom.mejor_tiempo_de_uso != blank -%}
<li><h4><span class="usoicon"></span>Mejor uso en</h4>
{% for mejor_tiempo_de_uso in product.metafields.custom.mejor_tiempo_de_uso.value %}
<p><span><a href="/collections/{{ mejor_tiempo_de_uso | handle }}">{{ mejor_tiempo_de_uso }},</a></span>
{% endfor %}
{%- else -%}
{%- endif %}
</p>
</li>

{% if product.metafields.custom.concentracion != blank -%}
<li><h4><span class="consetracionicon"></span>Concentración</h4>
<p><a href="/collections/{{ product.metafields.custom.concentracion | handle }}">{{ product.metafields.custom.concentracion | metafield_text }}</a>
{%- else -%}
{%- endif %}
</p>
</li>

{% if product.metafields.custom.a_o_de_lanzamiento != blank -%}
<li><h4><span class="anoicon"></span>Año de lanzamiento</h4>
<p><a href="/collections/{{ product.metafields.custom.a_o_de_lanzamiento | handle }}">{{ product.metafields.custom.a_o_de_lanzamiento | metafield_text }}</a>
{%- else -%}
{%- endif %}
</p>
</li>

{% if product.metafields.custom.pa_s != blank -%}
<li><h4><span class="paisicon"></span>País</h4>
<p><a href="/collections/{{ product.metafields.custom.pa_s | handle }}">{{ product.metafields.custom.pa_s | metafield_text }}</a>
{%- else -%}
{%- endif %}
</p>
</li>

{% if product.metafields.custom.perfumista != blank -%}
<li><h4><span class="perfumistaicon"></span>Perfumista</h4>
<p><a href="/collections/{{ product.metafields.custom.perfumista | handle }}">{{ product.metafields.custom.perfumista| metafield_text }}</a>
{%- else -%}
{%- endif %}
</p>
</li>

{% if product.metafields.custom.notas_de_salida != blank -%}
<li><h4><span class="notas-salidaicon"></span>Notas de salida</h4>
<p>{{ product.metafields.custom.notas_de_salida | metafield_text }}
{%- else -%}
{%- endif %}
</p>
</li>

 {% if product.metafields.custom.notas_de_corazon != blank -%}
<li><h4><span class="notas-corazonicon"></span>Notas de corazón</h4>
<p>{{ product.metafields.custom.notas_de_corazon | metafield_text }}
{%- else -%}
{%- endif %}
</p>
</li>

{% if product.metafields.custom.notas_de_base != blank -%}
<li><h4><span class="notas-baseicon"></span>Notas de base</h4>
<p>{{ product.metafields.custom.notas_de_base | metafield_text }}
{%- else -%}
{%- endif %}
</p>
</li>
</ul>
</div>
</div>
</div>
